n = ''

maior     = 0
sequencia = 0
indices   = ''

for x in range(0, len(n) - 1):
    soma = 0
    dig   = ''
    ind   = ''

    for y in range(x, x + 5):

        if(y <= len(n) - 1):
            dig  += n[y] + ' '
            soma += int(n[y])
            ind  += str(y) + ' '
    
    if(soma > maior):
        maior     = soma
        sequencia = dig
        indices   = ind

print(f'O tamanho da String é de {len(n)}')
print(f'A maior soma de 5 dígitos consecutivos é de {maior} pela sequência {sequencia}')
print(f'Os índices dos números da sequência não string são: {indices}')